Bash(npx tsx -e "
import { getSeedParticipants } from ''./lib/arena/seedData'';
const participants = getSeedParticipants\(\);
const sorted = [...participants].sort\(\(a, b\) => b.returnPct - a.returnPct\);
console.log\(''Rank  Name               Days  Return   Peak     Trough''\);
for \(let i = 0; i < sorted.length; i++\) {
  const p = sorted[i];
  let maxVal = 0, minVal = Infinity;
  for \(const d of p.data\) { if \(d.value > maxVal\) maxVal = d.value; if \(d.value < minVal\) minVal = d.value; }
  const daysAgo = Math.round\(\(Date.now\(\) - p.joinTime\) / \(24*60*60*1000\)\);
  const peak = \(\(maxVal/100000-1\)*100\).toFixed\(0\);
  const trough = \(\(minVal/100000-1\)*100\).toFixed\(0\);
  console.log\(\(''#''+\(i+1\)\).padEnd\(6\) + p.displayName.padEnd\(19\) + daysAgo.toString\(\).padStart\(3\)+''d'' + p.returnPct.toFixed\(0\).padStart\(7\)+''%'' + \(''+''+peak+''%''\).padStart\(9\) + \(trough+''%''\).padStart\(9\)\);
}
")

