import { createClient } from ''@supabase/supabase-js'';
import { readFileSync } from ''fs'';
import { resolve } from ''path'';
const envPath = resolve\(import.meta.dirname, ''.env.local''\);
const envContent = readFileSync\(envPath, ''utf-8''\);
for \(const line of envContent.split\(''\\n''\)\) {
  const trimmed = line.trim\(\);
  if \(!trimmed || trimmed.startsWith\(''#''\)\) continue;
  const eqIdx = trimmed.indexOf\(''=''\);
  if \(eqIdx === -1\) continue;
  const key = trimmed.slice\(0, eqIdx\);
  const val = trimmed.slice\(eqIdx + 1\);
  if \(!process.env[key]\) process.env[key] = val;
}
const supabase = createClient\(process.env.NEXT_PUBLIC_SUPABASE_URL!, process.env.SUPABASE_SERVICE_ROLE_KEY!, { auth: { autoRefreshToken: false, persistSession: false } }\);
const { error } = await supabase.from\(''profiles''\).update\({ username: ''marcus1999'', display_name: ''marcus1999'' }\).eq\(''id'', ''b657704f-caea-4677-a1d3-08b228c3dad5''\);
console.log\(error ? ''Error: '' + error.message : ''Profile updated to marcus1999''\);
")
